mapper.select:
select userId, name, refreshToken
from user
where userId = #{userId} and password = #{password}

select name, userId, birthYear, sex, type, emailAccount, emailDomain, createdTime
from user
where userId = #{userId}

mapper.update:
update user
set refreshToken = #{token}
where userId = #{userId};

